package com.gainingskills.tylorapp;

import androidx.annotation.NonNull;
import androidx.appcompat.app.AppCompatActivity;

import android.content.Intent;
import android.os.Bundle;
import android.view.View;
import android.view.WindowManager;
import android.widget.EditText;
import android.widget.Toast;

import com.google.android.gms.tasks.OnCompleteListener;
import com.google.android.gms.tasks.Task;
import com.google.firebase.auth.AuthResult;
import com.google.firebase.auth.FirebaseAuth;
import com.google.firebase.database.DatabaseReference;
import com.google.firebase.database.FirebaseDatabase;

import java.util.HashMap;

import soup.neumorphism.NeumorphButton;

public class SignUp_Activity extends AppCompatActivity {

    EditText PhoneNumber,Name,Email,Password;
    String email,password,phone,name;
    private FirebaseAuth mAuth;
    FirebaseDatabase database;
    DatabaseReference reference;
    String uid;
    NeumorphButton button;


    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_sign_up);
        this.getWindow().setSoftInputMode(WindowManager.LayoutParams.SOFT_INPUT_STATE_ALWAYS_HIDDEN);
        initViews();
        // Initialize Firebase Auth
        mAuth = FirebaseAuth.getInstance();
        database = FirebaseDatabase.getInstance();
        reference = database.getReference("User");
        CreateUser();





    }

    private void CreateUser() {
        button.setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View v) {
                button.setClickable(false);
                button.setText("wait");
                email = Email.getText().toString().trim();
                password = Password.getText().toString().trim();
                name = Name.getText().toString().trim();
                phone = PhoneNumber.getText().toString().trim();

                mAuth.createUserWithEmailAndPassword(email,password).addOnCompleteListener(SignUp_Activity.this, new OnCompleteListener<AuthResult>() {
                    @Override
                    public void onComplete(@NonNull Task<AuthResult> task) {
                        uid = FirebaseAuth.getInstance().getUid();
                        if (task.isSuccessful()){


                            HashMap<String, Object> map1 = new HashMap<>();
                            map1.put("name", name);
                            map1.put("password", password);
                            map1.put("email", email);
                            map1.put("phone", phone);
                            map1.put("uid", uid);
                            map1.put("address", "Address not given");
                            map1.put("image","data:image/png;base64,");

                            reference.child(uid).setValue(map1).addOnCompleteListener(new OnCompleteListener<Void>() {
                                @Override
                                public void onComplete(@NonNull Task<Void> task) {



                                    DatabaseReference root = FirebaseDatabase.getInstance().getReference("User").child(uid).child("measurement");

                                    HashMap<String, Object> map = new HashMap<>();
                                    map.put("collar", "");
                                    map.put("chest", "");
                                    map.put("waist", "");
                                    map.put("seat", "");
                                    map.put("frontWidth", "");
                                    map.put("firstBtn", "");
                                    map.put("sleeveLength", "");
                                    map.put("shortSleeve", "");
                                    map.put("shortSleeveOpen", "");
                                    map.put("biceps", "");
                                    map.put("forearm", "");
                                    map.put("armhole", "");
                                    map.put("shoulder", "");
                                    map.put("backWidth", "");
                                    map.put("shirtLength", "");
                                    map.put("cuff", "");

                                    root.child(uid).setValue(map).addOnCompleteListener(new OnCompleteListener<Void>() {
                                        @Override
                                        public void onComplete(@NonNull Task<Void> task) {

                                            Toast.makeText(SignUp_Activity.this, "Your Profiles is successFully updated.", Toast.LENGTH_LONG).show();
                                            startActivity(new Intent(SignUp_Activity.this, UserProfileActivity.class));
                                            finish();

                                        }
                                    });

                                }
                            });


                        } else {
                            button.setClickable(true);
                            button.setText("Register");
                            Toast.makeText(SignUp_Activity.this, "Error: check your internet connection", Toast.LENGTH_SHORT).show();
                        }
                    }
                });
            }
        });

    }

    private void initViews() {
        PhoneNumber = findViewById(R.id.phoneEd);
        Name = findViewById(R.id.fullname);
        Email = findViewById(R.id.email);
        Password = findViewById(R.id.password);
        button = findViewById(R.id.registerbtn);
    }
}